function clearLiveSearch() {
	$('#us_livesearch_close').removeClass('visible');
	$('#us_livesearch').removeClass('expanded');
	$('#us_livesearch').html('');
	$('#input_search').val('');
}

$(document).ready(function() {
	let timer, delay = 500;

	$('#input_search').keyup(function() {
	    let _this = $(this);

	    clearTimeout(timer);

	    timer = setTimeout(function() {
	        let value = $('header #search input[name=\'search\']').val();

			if (value.length > 2) {
				let key = encodeURIComponent(value);
				octsearch.search(key, 'desktop');
			}

	    }, delay );
	});
});

body.script:
			function getOctPolicy() {
				$.ajax({
					url: "index.php?route=common/footer/getOctPolicy",
					type: "post",
					dataType: "json",
					cache: false,
					success: function(t) {
						if (t['text_oct_policy']) {
							var html = '




'+ t['text_oct_policy'] +''+ t['oct_policy_accept'] +'';

							$('body').append(html);

							$('#oct-policy-btn').on('click', function () {
								$('#oct-policy').addClass('hidden');
								const date = new Date(t['oct_policy_day_now'].replace(/-/g, "/"));
								date.setTime(date.getTime() + (t['oct_max_day'] * 24 * 60 * 60 * 1000));
								document.cookie = t['oct_policy_value']+'=1; path=/; expires=' + date.toUTCString();
							});
						}
					},
					error: function(e, t, i) {
						alert(i + "\r\n" + e.statusText + "\r\n" + e.responseText)
					}
				});
			}

			$(function() {
				if (getOCTCookie('oct_policy') == 'undefined') {
					getOctPolicy();
				}
			});

function update_qty_options() {
    $('.owq-option input[type="checkbox"]').each(function() {
        $qty = $(this).closest('tr').find('.owq-input');
        opt_qty = Number($qty.val()) || 0;

        if ($qty.data('max') && opt_qty > $qty.data('max')) {
            $qty.closest('tr').addClass('no-stock');
        } else {
            $qty.closest('tr').removeClass('no-stock');
        }

        if ($(this).data('id') && opt_qty > 0) {
            $(this).val($(this).data('id') + $(this).data('split') + opt_qty).data('price', $(this).data('fprice') * opt_qty).prop('checked', true);
        } else {
            $(this).prop('checked', false);
        }
    });
    $('.owq-option select').each(function() {
        $qty = $(this).closest('div').find('.owq-input');
        opt_qty = Number($qty.val()) || 0;

        $(this).find('option').each(function(){
            if ($(this).data('id')) {
                if (opt_qty < 1) opt_qty = 1;
                $(this).val($(this).data('id') + '|' + opt_qty).data('price', $(this).data('fprice') * opt_qty);
            } else {
                $(this).val('').data('price', 0);
            }
        });
    });
    if ($('.owq-option input[type="checkbox"]').length > 0) {
        $('.owq-option input[type="checkbox"]').trigger('change');
    } else {
        $('.owq-option select').trigger('change');
    }
}
$(document).ready(function(){
    $(document).on('input', '.owq-option .owq-input', function(){
        update_qty_options();
    }).on('click', '.owq-quantity .owq-add', function() {
        $input = $(this).prev();
        qty = (Number($input.val()) || 0)+1;
                if ($input.data('max') && qty > $input.data('max')) qty = $input.data('max');
                $input.val(qty).trigger('input');
    }).on('click', '.owq-quantity .owq-sub', function() {
        $input = $(this).next();
        qty = (Number($input.val()) || 0)-1;
        if (qty < 1) qty = '';
        $input.val(qty).trigger('input');
    }).on('input', '.owq-area-hw input[type="text"]', function(){
        $div = $(this).closest('div.form-group');
        $width = $div.find('.owq-input-width');
        $height = $div.find('.owq-input-height');
        $error = $div.find('.owq-error');
        $div.removeClass('has-error').find('.has-error').removeClass('has-error');
        $div.find('.text-danger').remove();
        $checkbox = $div.find('input[type="checkbox"]');
        width = Number($width.val()) || 0;
        height = Number($height.val()) || 0;        
        $checkbox.prop('checked', false);
        if (width && height) {
            if ($checkbox.data('min-width') && width < $checkbox.data('min-width')) {
                $width.closest('td').addClass('has-error');
                $error.html('
Минимум '+$checkbox.data('min-width')+' '+$checkbox.data('unit')+'');
            } else if ($checkbox.data('max-width') && width > $checkbox.data('max-width')) {
                $width.closest('td').addClass('has-error');
                $error.html('
Максимум '+$checkbox.data('max-width')+' '+$checkbox.data('unit')+'');
            } else if ($checkbox.data('min-height') && height < $checkbox.data('min-height')) {
                $height.closest('td').addClass('has-error');
                $error.html('
Минимум '+$checkbox.data('min-height')+' '+$checkbox.data('unit')+'');
            } else if ($checkbox.data('max-height') && height > $checkbox.data('max-height')) {
                $height.closest('td').addClass('has-error');
                $error.html('
Максимум '+$checkbox.data('max-height')+' '+$checkbox.data('unit')+'');
            } else {
                var area = width * height * $checkbox.data('mul') * $checkbox.data('mul');
                $checkbox.data('area', area).data('price', area);
                $checkbox.val(width + 'x' + height);
                $checkbox.prop('checked', true);
            }
        }                
        $checkbox.trigger('change');
    });    
    update_qty_options();
    $('.owq-area-hw .owq-input-width').trigger('input');
});

